mapper.update:
UPDATE users
SET userAddress = #{userAddress},
userPhone = #{userPhone}, userEmail =
#{userEmail}
WHERE userNickname =
#{userNickname}

mapper.select:
SELECT *
FROM
payment_list
WHERE userNickname = #{userNickname}
AND paymentid = (
SELECT MAX(paymentid)
FROM payment_list
WHERE userNickname =
#{userNickname}
)

mapper.update:
UPDATE basket
SET quantity = #{quantity}
WHERE basketid
= #{basketid}

mapper.select:
SELECT basketid
FROM orders
WHERE paymentid = #{paymentid}
